INSERT INTO `slider` (`id`, `title_hy`, `title_ru`, `title_de`, `title_fr`, `title_es`, `title_ar`, `title_fa`, `title_en`, `short_description_en`, `short_description_hy`, `short_description_ru`, `short_description_de`, `short_description_fr`, `short_description_es`, `short_description_ar`, `short_description_fa`, `thumbnail_base_url`, `thumbnail_path`, `link`, `created_at`, `updated_at`, `published_at`) VALUES
(1, ' aaadsad   ', '', '', '', '', '', '', 'Culture', 'Artsakh is actually an open air museum. Thousands of ancient architectural monuments (the age of many of which more than 11-12 centuries, and some of them thousands of years), including  pearls of medieval architecture - Gandzasar, Dadivank, Amaras Gtchavank, admire with beauty and unique stone carvings, call to adore the genius art of architectural solutions of the ancient masters.', ' aaadsad    aaadsad    aaadsad    aaadsad    aaadsad    aaadsad    aaadsad   ', '', '', '', '', '', '', 'http://storage.artsakh.travel/source', '1/XLR05bUcLZ9RDdWcDf1P6qCZOUgpoGLH.jpg', '/discover/culture', 1515182602, 1520367249, 1515182602),
(2, 'Hayeren Hayeren Hayeren Hayeren ', '', '', '', '', '', '', 'Events', 'May 9 is a triple holiday - the Day of Victory in the Great Patriotic War (the Armenians of Artsakh were directly involved in World War II), Shoushi Liberation Day and Foundation Day of the Defense Army of Artsakh. The military parade is held in the Renaissance Square, events and a variety of cultural and sporting events is hosted.', 'Hayeren Hayeren Hayeren Hayeren Hayeren Hayeren Hayeren Hayeren Hayeren ', '', '', '', '', '', '', 'http://storage.artsakh.travel/source', '1/zFzpDBHCt4IlAUmLzbCmOR03j0pmAwOv.jpg', '/about/national-holidays-and-festivals', 1516715077, 1520367280, 1516715036),
(3, '', '', '', '', '', '', '', 'Cuisine', 'Karabakh cuisine, as well as the history of Karabakh, is rooted in antiquity. It is a wonderful combination of all kinds of tastes and aromas inherent only in an atmosphere saturated with sun, warm hearts and smiles of Karabakh people. The cuisine is mostly composed of a variety of dishes that are made from meat and vegetables growing on the hills and valleys of Karabakh. ', '', '', '', '', '', '', '', 'http://storage.artsakh.travel/source', '1/LjQj2Iex02QwAPrnMJQ6kAKxSTVHCyMO.jpg', '/discover/cuisine', 1520366122, 1520366122, 1520366122),
(4, '', '', '', '', '', '', '', 'Adventures', 'Artsakh gives an opportunity for all stakeholders to try adventure tourism. Karabakh is not famous for its rich ancient history and culture, but also the freedom-loving, even rebellious, adventurous kind of native. ', '', '', '', '', '', '', '', 'http://storage.artsakh.travel/source', '1/0bfEdJN93gVDWVaSp8dAD4avONjyEWRL.jpg', '/things-to-do/adventure', 1520366258, 1520366437, 1520283409);
